import { useState, useEffect } from 'react';
import { MoviesService } from "../api/MoviesService";

export const useDetails = (id) => {
    const [movies, setMovie] = useState ([])

    useEffect(() => {
      if (!movies[id])
        MoviesService.getMovieById()
          .then((res) => setMovies(res))
      }, []);

    return movies;

}
